import React from "react";

class Weather extends React.Component {
  state = { loading: true, temp: null };
  async getWeather() {
    const response = await fetch(
      fetch(
        "https://community-open-weather-map.p.rapidapi.com/weather?callback=test&id=2172797&units=imperial&mode=JSON&q=Des%20Moines",
        {
          method: "GET",
          headers: {
            "x-rapidapi-host": "community-open-weather-map.p.rapidapi.com",
            "x-rapidapi-key":
              "bceb01c09emsh7333c7d8acd9bbfp114330jsn9662e80626cf",
          },
        }
      )
        .then((response) => {
          console.log(response);
        })
        .catch((err) => {
          console.log(err);
        })
    );
    const data = await response.json();
    console.log(data);
    this.setState({ loading: false, temp: data.currently.temperature });
  }
  render() {
    return (
      <div>
        {this.state.loading || !this.state.temperature ? (
          <div>loading </div>
        ) : (
          <div>{this.state.temp} </div>
        )}{" "}
      </div>
    );
  }
}

export default Weather;
