import React from "react";

export default class Time extends React.Component {
  state = { curTime: null };

  componentDidMount() {
    setInterval(() => {
      this.setState({
        curTime: new Date().toLocaleString(),
      });
    }, 1000);
  }
  render() {
    return <div>{this.state.curTime}</div>;
  }
}
