import React from "react";
import Card from "react-bootstrap/Card";
const Weather = () => {
  let currentCast = {};
  let fiveDayCast = {};
  function getFiveDayForecast() {
    fetch(
      "https://community-open-weather-map.p.rapidapi.com/forecast?q=Des%20Moines&units=imperial",
      {
        method: "GET",
        headers: {
          "x-rapidapi-host": "community-open-weather-map.p.rapidapi.com",
          "x-rapidapi-key":
            "bceb01c09emsh7333c7d8acd9bbfp114330jsn9662e80626cf",
        },
      }
    )
      .then((response) => {
        this.fiveDayCast = response.json();
      })
      .catch((err) => {
        console.log(err);
      });
  }

  function getCurrentForecast() {
    fetch(
      "https://community-open-weather-map.p.rapidapi.com/weather?callback=test&id=2172797&units=%2522metric%2522%20or%20%2522imperial%2522&mode=xml%252C%20html&q=London%252Cuk",
      {
        method: "GET",
        headers: {
          "x-rapidapi-host": "community-open-weather-map.p.rapidapi.com",
          "x-rapidapi-key":
            "bceb01c09emsh7333c7d8acd9bbfp114330jsn9662e80626cf",
        },
      }
    )
      .then((response) => {
        this.currentCast = response.json();
        console.log(this.currentCast);
      })
      .catch((err) => {
        console.log(err);
      });
  }
  setInterval(getFiveDayForecast, 10000);
  setTimeout(getCurrentForecast, 20000);

  return (
    <div>
      <Card>
        <Card.Body>
          <Card.Title>{currentCast.main.temp}</Card.Title>
          <Card.Text>Bachelors of Science in Software Engineering</Card.Text>
        </Card.Body>
      </Card>
    </div>
  );
};

export default Weather;
